rss.onEnter:
  clearAllCache();
  displayInfos = "false";
  showIdle();
	Config = "/usr/local/etc/srjg.cfg";
    Config_ok = loadXMLFile(Config);

	if (Config_ok == null) {
		print("Load Config fail ", Config);
	}
    else {
        Config_itemSize = getXMLElementCount("Config");
	}

  if (Config_itemSize > 0) {
